import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';
import './Map.css';

mapboxgl.accessToken =
  'pk.eyJ1IjoieWFudHNlbnRlciIsImEiOiJjbG5wYzY4b2wwYTJmMmlvMTBqYjkyY2VoIn0.95vb98mxkUb4pzEpO_rF0Q';

// ... (your existing imports)

const Map = () => {
  const mapContainerRef = useRef(null);

  const [lng, setLng] = useState(5);
  const [lat, setLat] = useState(34);
  const [zoom, setZoom] = useState(1.5);

  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapContainerRef.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });

    map.addControl(new mapboxgl.NavigationControl(), 'top-right');

    map.on('load', () => {
      // Coordinates for a bounding box around Maine
      const maineCoords = [
        [-71.18507, 42.95642],
        [-66.93457, 42.95642],
        [-66.93457, 47.45985],
        [-71.18507, 47.45985],
        [-71.18507, 42.95642]
      ];

      map.addLayer({
        id: 'mainePolygon',
        type: 'fill',
        source: {
          type: 'geojson',
          data: {
            type: 'Feature',
            geometry: {
              type: 'Polygon',
              coordinates: [maineCoords]
            }
          }
        },
        paint: {
          'fill-color': '#FF5733',
          'fill-opacity': 0.4
        }
      });

      // Coordinates for a bounding box around New York State
      const newYorkCoords = [
        [-79.76215, 40.4774], 
        [-71.18507, 40.4774], 
        [-71.18507, 45.01585],
        [-79.76215, 45.01585],
        [-79.76215, 40.4774]
      ];

      map.addLayer({
        id: 'newYorkPolygon',
        type: 'fill',
        source: {
          type: 'geojson',
          data: {
            type: 'Feature',
            geometry: {
              type: 'Polygon',
              coordinates: [newYorkCoords]
            }
          }
        },
        paint: {
          'fill-color': '#3F51B5',  // A different color for differentiation
          'fill-opacity': 0.4
        }
      });
    });

    map.on('move', () => {
      setLng(map.getCenter().lng.toFixed(4));
      setLat(map.getCenter().lat.toFixed(4));
      setZoom(map.getZoom().toFixed(2));
    });

    return () => map.remove();
  }, []);

  return (
    <div>
      <div className='sidebarStyle'>
        <div>
          Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
        </div>
      </div>
      <div className='map-container' ref={mapContainerRef} />
    </div>
  );
};

export default Map;
